import React, {useState} from "react";
import Swal from "sweetalert2";
import BlockUI from "../BlockUI/BlockUI";
import "./App.css";

import AppContainer from "../../containers/App/AppContainer";

import { search } from "../../utils/Spotify"

const App = () => {
    const [tracks, setTracks] = useState([]);

    const popupMessage = (title, message, icon) => {
        Swal.fire(title, message, icon);
    }

    const toggleBlocking = () => {
        this.setState({ blocking: !this.state.blocking });
    }

    // const searchOnSpotify = (term) => {
    //     search(term).then(tracks => {
    //         setTracks((tracks));
    //     });
    // };

    const search = async (term) => {
        if (term.trim() === "") {
            this.popupMessage("Warning!", "Enter a search term.", "warning");
        } else {
            this.toggleBlocking();
            const response = await Spotify.search(term);
            this.toggleBlocking();

            if (!response) return;
            else if (response.length === 0)
                this.popupMessage("Error!", `No results found for: ${term}.`, "error");
            else this.setState({ searchResults: response });
        }
    }

    const playlistTracks = [];
    const addTrack = (track) => {
        if (
            this.playlistTracks.find((savedTrack) => savedTrack.id === track.id)
        ) {
            return;
        }
        //Track not found in PLaylist

        //Array copy
        let newPlaylistTrack = [...this.playlistTracks];
        newPlaylistTrack.push(track);
        this.setState({ playlistTracks: newPlaylistTrack });
    };

    return (
        <AppContainer
            searchOnSpotify={searchOnSpotify}
            tracks={tracks}
            onAdd={this.addTrack}
        />
    );
}

export default App;
