    const addTrack = () => {
        props.onAdd(props.track);
    }

    const removeTrack = () => {
        props.onRemove(props.track);
    }

    return (
        <div className="Track">
            <div className="Track-information">
                <h3> {props.track.name} </h3>
                <p>
                    {props.track.artist} | {props.track.album}
                </p>
            </div>
            {/*{this.renderAction()}*/}
            <button
                onClick={props.isRemoval ? props.onRemove(props.track) : props.addTrack(props.track)}
                className="Track-action"
            >
            {props.isRemoval ? (
                    <box-icon
                        color="white"
                        name="minus-circle"
                        animation="flashing-hover"
                    ></box-icon>
                ) : (
                    <box-icon
                        color="white"
                        name="plus-circle"
                        animation="flashing-hover"
                    ></box-icon>
                )}
            </button>
        </div>
    );
}

export default Track;
