import React, {useState} from "react";
import Swal from "sweetalert2";
import BlockUI from "../BlockUI/BlockUI";
import "./App.css";

import AppContainer from "../../containers/App/AppContainer";

import { search } from "../../utils/Spotify"

const App = () => {
    const [searchResults, setSearchResults] = useState([]);
    const [playlistTracks, setPlaylistTracks] = useState([]);
    const [playlistName, setPlaylistName] = useState("Playlist Name");
    const [blocking, setBlocking] = useState(false);

    const popupMessage = (title, message, icon) => {
        Swal.fire(title, message, icon);
    }

    const toggleBlocking = () => {
        setBlocking( !this.blocking );
    }

    const searchOnSpotify = async (term) => {
        if (term.trim() === "") {
            this.popupMessage("Warning!", "Enter a search term.", "warning");
        } else {
            this.toggleBlocking();
            const response = await search(term);
            this.toggleBlocking();

            if (!response) return;
            else if (response.length === 0)
                this.popupMessage("Error!", `No results found for: ${term}.`, "error");
            else setSearchResults( response );
        }
    }

    const addTrack = (track) => {
        if (
            this.playlistTracks.find((savedTrack) => savedTrack.id === track.id)
        ) {
            return;
        }
        //Track not found in PLaylist

        //Array copy
        let newPlaylistTrack = [...this.playlistTracks];
        newPlaylistTrack.push(track);
        setPlaylistTracks( newPlaylistTrack );
    };

    const removeTrack = (track) => {
        const newPlaylistTrack = this.playlistTracks.filter(
            (savedTrack) => savedTrack.id !== track.id
        );
        setPlaylistTracks( newPlaylistTrack );
    }

    const updatePlaylistName = (newName) => {
        setPlaylistName( newName );
    }

    const savePlaylist = async () => {
        const tracksUris = this.playlistTracks.map((track) => track.uri);
        const noTracks = tracksUris.length === 0;
        const noPlaylistName = this.playlistName.trim() === "";
        if (!noTracks && !noPlaylistName) {
            this.toggleBlocking();
            await savePlaylist(this.playlistName, tracksUris);
            this.toggleBlocking();

            this.setState({ playlistName: "Playlist Name", playlistTracks: [] });
            this.popupMessage("Saved!", "Playlist saved to your account.", "success");
        } else {
            if (noPlaylistName)
                this.popupMessage(
                    "Warning!",
                    "Choose a name for your playlist.",
                    "warning"
                );
            else if (noTracks)
                this.popupMessage("Warning!", "Add some tracks first.", "warning");
            else
                this.popupMessage("Warning!", "Create your playlist first.", "warning");
        }
    }

    // We don't render JSX in stateful components. We send JSX to stateless components.
    return (
        <AppContainer
            onSearch={searchOnSpotify}
            onAdd={this.addTrack}
            searchResults={this.searchResults}
            onRemove={this.removeTrack}
            onNameChange={this.updatePlaylistName}
            onSave={this.savePlaylist}
            playlistTracks={this.playlistTracks}
            playlistName={this.playlistName}
            blocking={blocking}
        />
    );
}

export default App;
