import React from "react";
import "boxicons";
import "./Track.css";

const Track = (props) => {
    // constructor(props) {
    //     super(props);
    //     this.addTrack = this.addTrack.bind(this);
    //     this.removeTrack = this.removeTrack.bind(this);
    // }
    const renderAction = () => {
        return (
            <button
                onClick={props.isRemoval ? this.removeTrack : this.addTrack}
                className="Track-action"
            >
                {props.isRemoval ? (
                    <box-icon
                        color="white"
                        name="minus-circle"
                        animation="flashing-hover"
                    ></box-icon>
                ) : (
                    <box-icon
                        color="white"
                        name="plus-circle"
                        animation="flashing-hover"
                    ></box-icon>
                )}
            </button>
        );
    }

    const addTrack = () => {
        props.onAdd(props.track);
    }

    const removeTrack = () => {
        props.onRemove(props.track);
    }

    return (
        <div className="Track">
            <div className="Track-information">
                <h3> {this.props.track.name} </h3>
                <p>
                    {this.props.track.artist} | {this.props.track.album}
                </p>
            </div>
            {this.renderAction()}
        </div>
    );
}

export default Track;
